import React, { useEffect, useState, Component } from 'react';
import axios from 'axios';

export default class App extends Component {
  constructor(){
    super()
    this.state={
      kullanıcılar:[],
      kullanıcı_ekranı:true,
      kullanıcı_ekleme_ekranı:false
    }
  }
  componentDidMount(){
    axios
    .get("https://jsonplaceholder.typicode.com/users")
    .then(res=>{this.setState({
      kullanıcılar:res.data
    })})
  }
  render() {
    const {kullanıcılar} =this.state;
    return (
      <div className='container'>
      {
        <div className='row'>
        <div className='col-12-l bg-info d-flex justify-content-center'><h1>Users</h1></div>
      <div className='row'>
        {
          kullanıcılar.map(kullanıcı=>{
            return(
              <div className='col-12-l bg-ligth'>{kullanıcı.name}</div>
            )
          })
        }
      </div>
      </div>}
         </div>
    )
  }
}
